import React, { useState } from 'react'

const IndexPage = () => {
    const [data, setData] = useState('');
    const handleChange = (e)=>{
        setData({...data,[e.target.name]:e.target.value})
    }
    const handleClick =(e)=>{
        e.preventDefault();
        setData({
            name:'',
            rollno:'',
            dept:'',
            noofsem:''
        });
        
        
    }
    
  return (
    <>
    <input type="text" name="name" id="name" placeholder='Student Name'value={data.name} onChange={handleChange}/>
    <input type="number" name="rollno" id="rollno" placeholder='Roll Number' value={data.rollno} onChange={handleChange}/>
    <input type="text" name="dept" id="dept" placeholder='Department' value={data.dept} onChange={handleChange}/>
    <input type="number" name="noofsem" id="noofsem" placeholder='Number of semesters' value={data.noofsem} onChange={handleChange}/>
    <button onClick={handleClick}>Next</button>
    </>
  )
}

export default IndexPage
