import React, { useState } from "react";
import styles from "./Login.module.css";
import { useNavigate, Link } from "react-router-dom";
import axios from "axios";

const Login = () => {
  const [form, setForm] = useState({ email: "", password: "", role: "" });
  const [errors, setErrors] = useState({});
  const navigate = useNavigate();

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
    setErrors({ ...errors, [e.target.name]: "" }); // clear field-specific error
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const tempErrors = {};
    if (!form.email.trim()) tempErrors.email = "Email is required";
    if (!form.password.trim()) tempErrors.password = "Password is required";
    if (!form.role.trim()) tempErrors.role = "Select Role";

    if (Object.keys(tempErrors).length > 0) {
      setErrors(tempErrors);
      return;
    }

    axios
      .post("http://localhost:8080/users/login", form)
      .then((response) => {
        const user = response.data;

        // Check if user exists
        if (!user || !user.userId) {
          setErrors({ email: "Wrong email", password: "Wrong password", role: "Invalid role" });
          return;
        }

        // Match email
        if (user.email !== form.email) {
          setErrors({ email: "Wrong email" });
          return;
        }

        // Match password
        if (user.password !== form.password) {
          setErrors({ password: "Wrong password" });
          return;
        }

        // Match role
        if (user.role !== form.role) {
          setErrors({ role: "Invalid role" });
          return;
        }

        // Save to local storage
        localStorage.setItem("userId", user.userId);

        // Navigate to role-based dashboard
        switch (user.role) {
          case "Super Admin":
            navigate("/superadmindashboard");
            break;
          case "Admin":
            navigate("/admindashboard");
            break;
          case "Resident":
            navigate("/userdashboard");
            break;
          default:
            setErrors({ role: "Invalid role" });
        }

        // Clear form after success
        setForm({ email: "", password: "", role: "" });
      })
      .catch((error) => {
        console.error("Login failed:", error);
        setErrors({ email: "Login failed", password: "Login failed", role: "Login failed" });
      });
  };

  return (
    <>
      <h1 className={styles.head}>My Colony</h1>
      <form className={styles.login} onSubmit={handleSubmit}>
        <h1>Login</h1>

        <input
          type="email"
          name="email"
          className={errors.email ? styles.errorInput : ""}
          placeholder={errors.email || "Email Address"}
          value={form.email}
          onChange={handleChange}
        />

        <input
          type="password"
          name="password"
          className={errors.password ? styles.errorInput : ""}
          placeholder={errors.password || "Password"}
          value={form.password}
          onChange={handleChange}
        />

        <select
          name="role"
          className={errors.role ? styles.errorInput : ""}
          value={form.role}
          onChange={handleChange}
        >
          <option value="">{errors.role || "Select Role"}</option>
          <option value="Admin">Admin</option>
          <option value="Super Admin">Super Admin</option>
          <option value="Resident">Resident</option>
        </select>

        <div className={styles.links}>
          <Link to="/forgot">Forgot Password?</Link>
        </div>

        <button type="submit">Log in</button>
      </form>
    </>
  );
};

export default Login;
