import React, { useState } from 'react';
import styles from './ManageServices.module.css';
import axios from 'axios';

const ManageServices = () => {
  const [Service, setService] = useState({
    serviceName:''
  });

  const addService = () => {
  if (newService) {
    const serviceData = {
      name: newService
    };

    axios.post("http://localhost:8080/request/save", serviceData)
      .then((response) => {
        console.log("Service added:", response.data);
        setNewService('');
      })
      .catch((error) => {
        console.error("Error adding service:", error);
      });
  }
};
  


  return (
    <div className={styles.services}>
      <h3>Manage Services</h3>
      <input type="text" name="serviceName"placeholder="New Service" value={newService.serviceName} onChange={(e) => setNewService(e.target.value)}/>
      <button onClick={addService}>Add Service</button>
            <button >Remove</button>
    </div>
  );
};

export default ManageServices;
