import React, { useState } from "react";

const StudentForm = ({onNext}) => {
  const [data, setData] = useState({
    name: "",
    roll: "",
    dept: "",
    semCount: "",
  });
  const handleChange = (e) => {
    setData({ ...data, [e.target.name]: e.target.value });
  };
  const handleSubmit = () => {
    if (!data.name || !data.roll || !data.dept || !data.semCount) {
      return;
    }
    onNext(data);
    setData({
      name: "",
      roll: "",
      dept: "",
      semCount: "",
    });
  };
  return (
    <>
      <input type="text" name="name" id="name" placeholder="Student Name" value={data.name} onChange={handleChange}/>
      <input type="number" name="roll" id="roll" placeholder="Roll No" value={data.roll} onChange={handleChange}/>
      <input type="text" name="dept" id="dept" placeholder="Department" value={data.dept} onChange={handleChange}/>
      <input type="number" name="semCount" id="semCount" placeholder="Number of Semesters" value={data.semCount} onChange={handleChange}/>
      <button onClick={handleSubmit}>Next</button>
    </>
  );
};

export default StudentForm;
