import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const StudentForm = () => {
  const navigate = useNavigate();
  const [form, setForm] = useState({
    name: '',
    roll: '',
    dept: '',
    semCount: ''
  });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Save form to localStorage for Subject.jsx to use
    localStorage.setItem('studentForm', JSON.stringify(form));

    // Clear form
    setForm({
      name: '',
      roll: '',
      dept: '',
      semCount: ''
    });
    navigate('/subject');
  };

  return (
    <>
      <form onSubmit={handleSubmit}>
        <input type="text" name="name" placeholder="Student Name" value={form.name} onChange={handleChange} required />
        <input type="number" name="roll" placeholder="Register Number" value={form.roll} onChange={handleChange} required />
        <input type="text" name="dept" placeholder="Department" value={form.dept} onChange={handleChange} required />
        <input type="number" name="semCount" placeholder="No of Semesters" value={form.semCount} onChange={handleChange} required />
        <button type="submit">Next</button>
      </form>
    </>
  );
};

export default StudentForm;
