import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import styles from './RequestDetail.module.css';

const RequestDetail = () => {
  const [request, setRequest] = useState(null);
  const [blockAdmin, setBlockAdmin] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { id } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    if (!id) {
      setError('No request ID provided');
      setLoading(false);
      return;
    }

    // Fetch request details
    axios.get(`http://localhost:8080/requests/${id}`)
      .then(requestResponse => {
        if (!requestResponse.data) throw new Error('No request data received');
        setRequest(requestResponse.data);

        // Fetch admin for the block
        const block = requestResponse.data.blockName;
        if (block) {
          axios.get(`http://localhost:8080/users/block/${block}`)
            .then(adminResponse => {
              if (adminResponse.data && adminResponse.data.length > 0) {
                // Find the admin user (role === 'ADMIN')
                const admin = adminResponse.data.find(user => 
                  user.role && user.role.toUpperCase() === 'ADMIN'
                );
                if (admin) {
                  setBlockAdmin(admin);
                  console.log('Admin found:', admin);
                } else {
                  console.log('No admin user found for this block');
                }
              }
            })
            .catch(adminError => {
              console.error('Error fetching admin:', adminError);
            });
        }

        setLoading(false);
      })
      .catch(error => {
        console.error('Error fetching request:', error);
        setError(error.response?.data?.message || error.message);
        setLoading(false);
      });
  }, [id]);

  const handleEdit = () => {
    navigate(`/editrequest/${id}`, { state: { request } });
  };

  const handleDelete = () => {
    if (window.confirm('Are you sure you want to delete this request?')) {
      axios.delete(`http://localhost:8080/requests/delete/${id}`)
        .then(() => {
          alert('Request deleted successfully');
          navigate('/viewrequest', { replace: true });
        })
        .catch(error => {
          console.error('Error deleting request:', error);
          alert(`Failed to delete request: ${error.response?.data?.message || error.message}`);
        });
    }
  };

  const handleBack = () => {
    navigate('/viewrequest');
  };

  const formatDate = (dateString) => {
    if (!dateString) return 'Not specified';
    try {
      const date = new Date(dateString);
      return date.toLocaleString('en-US', {
        year: 'numeric',
        month: 'long',
        day: 'numeric',
        hour: '2-digit',
        minute: '2-digit'
      });
    } catch (e) {
      console.error('Error formatting date:', e);
      return dateString;
    }
    console.log(formatDate);
    
  };

  if (loading) return <div className={styles.detailContainer}>Loading...</div>;
  if (error) return <div className={styles.detailContainer}>Error: {error}</div>;
  if (!request) return <div className={styles.detailContainer}>No request found</div>;

  return (
    <div className={styles.detailContainer}>
      <h3>Request Details</h3>

      <div className={styles.detailGrid}>
        {/* Existing request details... */}
        {/* ... keep all your existing request detail items ... */}
      </div>

      {/* Enhanced Block Admin Section */}
      {blockAdmin ? (
        <div className={styles.adminSection}>
          <h4>Block Administrator Details</h4>
          <div className={styles.detailGrid}>
            <div className={styles.detailItem}>
              <span className={styles.detailLabel}>Username:</span>
              <span className={styles.detailValue}>{blockAdmin.username || 'Not available'}</span>
            </div>
            <div className={styles.detailItem}>
              <span className={styles.detailLabel}>Name:</span>
              <span className={styles.detailValue}>{blockAdmin.name || 'Not available'}</span>
            </div>
            <div className={styles.detailItem}>
              <span className={styles.detailLabel}>Email:</span>
              <span className={styles.detailValue}>
                {blockAdmin.email ? (
                  <a href={`mailto:${blockAdmin.email}`} className={styles.contactLink}>
                    {blockAdmin.email}
                  </a>
                ) : 'Not available'}
              </span>
            </div>
            <div className={styles.detailItem}>
              <span className={styles.detailLabel}>Mobile:</span>
              <span className={styles.detailValue}>
                {blockAdmin.mobileNo ? (
                  <a href={`tel:${blockAdmin.mobileNo}`} className={styles.contactLink}>
                    {blockAdmin.mobileNo}
                  </a>
                ) : 'Not available'}
              </span>
            </div>
            <div className={styles.detailItem}>
              <span className={styles.detailLabel}>Block:</span>
              <span className={styles.detailValue}>{blockAdmin.block || 'Not specified'}</span>
            </div>
          </div>
        </div>
      ) : request.blockName ? (
        <div className={styles.adminSection}>
          <p>No admin information available for {request.blockName} block</p>
        </div>
      ) : null}

      <div className={styles.buttonGroup}>
        <button onClick={handleEdit} className={styles.editButton}>Edit</button>
        <button onClick={handleDelete} className={styles.deleteButton}>Delete</button>
        <button onClick={handleBack} className={styles.backButton}>Back to Requests</button>
      </div>
    </div>
  );
};

export default RequestDetail;
