// components/MyRequests.jsx
import React, { useEffect, useState } from 'react';
import styles from './MyRequests.module.css';
import RequestDetail from './RequestDetails';

const MyRequests = () => {
  const [requests, setRequests] = useState([]);
  const [selectedRequest, setSelectedRequest] = useState(null);

  useEffect(() => {
    // Simulated API data
    const dummyRequests = [
      { id: 1, type: 'Plumbing', status: 'Pending', description: 'Leak in kitchen' },
      { id: 2, type: 'Electricity', status: 'In Progress', description: 'Light not working' },
    ];
    setRequests(dummyRequests);
  }, []);

  return (
    <div className={styles.container}>
      <h2>My Service Requests</h2>
      <ul className={styles.requestList}>
        {requests.map((req) => (
          <li key={req.id} className={styles.requestItem} onClick={() => setSelectedRequest(req)}>
            <div>{req.type}</div>
            <div className={styles.status}>{req.status}</div>
          </li>
        ))}
      </ul>

      {selectedRequest && (
        <RequestDetails request={selectedRequest} onClose={() => setSelectedRequest(null)} />
      )}
    </div>
  );
};

export default MyRequests;
