import React from 'react';

const Tables = () => {
  function buy() {
    const selected = document.querySelectorAll('input[type="checkbox"]:');
    if (selected.length === 0) {
      alert("Please select at least one fruit before buying.");
    } else {
      alert("Items added successfully.");
      window.location.reload(); // Refreshes the page immediately
    }
  }

  const fruits = [
    { name: "Strawberry", rate: 200 },
    { name: "Blue Berry", rate: 250 },
    { name: "Black Berry", rate: 300 },
    { name: "Mul Berry", rate: 400 }
  ];

  return (
    <>
      <div style={{ textAlign: "center" }}>
        <h1>Fruits Name</h1>
        <table border="2px">
          <thead>
            <tr>
              <th>S.No</th>
              <th>Fruit Name</th>
              <th>Rate/KG</th>
              <th>Add to Cart</th>
            </tr>
          </thead>
          <tbody>
            {fruits.map((fruit, index) => (
              <tr key={index}>
                <td>{index + 1}</td>
                <td>{fruit.name}</td>
                <td>{fruit.rate}</td>
                <td><input type="checkbox" /></td>
              </tr>
            ))}
          </tbody>
        </table>
        <button type="button" onClick={buy}>Buy</button>
      </div>
    </>
  );
};

export default Tables;
