import React, { useState } from "react";
import styles from "./Login.module.css";
import { useNavigate, Link } from "react-router-dom";
import axios from "axios";

const Login = () => {
  const [form, setForm] = useState({ email: "", password: "", role: "" });
  const [errors, setErrors] = useState({});
  const [message, setMessage] = useState("");
  const navigate = useNavigate();

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
    setErrors({ ...errors, [e.target.name]: "" }); // Clear error on change
    setMessage("");
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    let tempErrors = {};
    if (!form.email) tempErrors.email = "Email is required";
    if (!form.password) tempErrors.password = "Password is required";
    if (!form.role) tempErrors.role = "Please select a role";

    if (Object.keys(tempErrors).length > 0) {
      setErrors(tempErrors);
      return;
    }

    axios
      .post("http://localhost:8080/users/login", form)
      .then((response) => {
        const user = response.data;

        if (!user || !user.userId) {
          setMessage("Invalid credentials");
          return;
        }

        if (user.email !== form.email) {
          setErrors({ email: "Wrong email" });
          return;
        }

        if (user.password !== form.password) {
          setErrors({ password: "Wrong password" });
          return;
        }

        if (user.role !== form.role) {
          setErrors({ role: "Invalid role" });
          return;
        }

        localStorage.setItem("userId", user.userId);

        switch (user.role) {
          case "Super Admin":
            navigate("/superadmindashboard");
            break;
          case "Admin":
            navigate("/admindashboard");
            break;
          case "Resident":
            navigate("/userdashboard");
            break;
          default:
            setMessage("Invalid credentials");
        }
      })
      .catch((error) => {
        console.error("Login failed:", error);
        setMessage("Invalid credentials");
      });

    // Clear fields after submit
    setForm({ email: "", password: "", role: "" });
  };

  return (
    <>
      <h1 className={styles.head}>My Colony</h1>
      <form className={styles.login} onSubmit={handleSubmit}>
        <h1>Login</h1>

        <input
          type="email"
          name="email"
          placeholder="Email Address"
          value={form.email}
          onChange={handleChange}
          required
        />
        {errors.email && <p className={styles.error}>{errors.email}</p>}

        <input
          type="password"
          name="password"
          placeholder="Password"
          value={form.password}
          onChange={handleChange}
          required
        />
        {errors.password && <p className={styles.error}>{errors.password}</p>}

        <select name="role" value={form.role} onChange={handleChange} required>
          <option value="">Select Role</option>
          <option value="Admin">Admin</option>
          <option value="Super Admin">Super Admin</option>
          <option value="Resident">Resident</option>
        </select>
        {errors.role && <p className={styles.error}>{errors.role}</p>}

        {message && <p className={styles.error}>{message}</p>}

        <div className={styles.links}>
          <Link to="/forgot">Forgot Password?</Link>
        </div>

        <button type="submit">Log in</button>
      </form>
    </>
  );
};

export default Login;
