import React, { useState } from 'react'

const Forgot = () => {
  const [email,setEmail] = useState('');
  const handleSubmit = (e)=>{
    e.preventDefault();
          if(email===''){
            alert("Email is required");
          }
          else{
            alert(`Email is sent to ${email}`);
            setEmail('');
          }
  };
  return (
    <>
    <div className={styles.forgotContainer}>
      <h2>Forgot Password</h2>
      <form onSubmit={handleSubmit}>
        <input type="email" name="email" id="email"value={email} onChange={(e)=>{setEmail(e.target.value)}}className={styles.forgotInput} placeholder='Enter your email' />
        <button type="submit" className={styles.forgotButton}>Submit</button>
      </form>
    </div>
    </>
  )
}

export default Forgotce
