import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import styles from './RaiseRequest.module.css';

const RaiseRequest = () => {
  const [serviceId, setServiceId] = useState('');
  const [description, setDescription] = useState('');
  const [message, setMessage] = useState('');
  const [isError, setIsError] = useState(false);
  const navigate = useNavigate();
  const userId = localStorage.getItem('userId');

  const handleSubmit = async () => {
    if (!serviceId || !description) {
      setMessage('Please fill all fields');
      setIsError(true);
      setTimeout(() => setMessage(''), 3000);
      return;
    }

    try {
      const newRequest = {
        serviceId: parseInt(serviceId),
        userId: parseInt(userId),
        description: description,
        status: "Pending",
        createDate: new Date().toISOString(),
        exceptCompletionDate: new Date(Date.now() + 86400000).toISOString()
        
      };
      const now = new Date();
const localISO = new Date(now.getTime() - now.getTimezoneOffset() * 60000).toISOString();

console.log(localISO); // This will reflect your local time


      await axios.post(`http://localhost:8080/requests/save`, newRequest);
      setServiceId('');
      setDescription('');
      setIsError(false);
      setMessage('Request submitted successfully!');
      setTimeout(() => setMessage(''), 3000);
    } catch (error) {
      console.error("Error submitting request:", error);
      setMessage('Something went wrong. Try again.');
      setIsError(true);
      setTimeout(() => setMessage(''), 3000);
    }
  };

  return (
    <div className={styles.requestForm}>
      <h3>Raise Request</h3>
      <select
        value={serviceId}
        onChange={(e) => setServiceId(e.target.value)}
        className={styles.input}
        required
      >
        <option value="">Select Service</option>
        <option value="1">Plumbing</option>
        <option value="2">Electricity</option>
        <option value="3">Garbage Collection</option>
        <option value="4">Maintenance</option>
      </select>

      <textarea
        placeholder="Description"
        value={description}
        onChange={(e) => setDescription(e.target.value)}
        className={styles.textarea}
        required
      />

      <button onClick={handleSubmit} className={styles.submitButton}>
        Submit
      </button>

      {message && (
        <p className={isError ? styles.errorMsg : styles.successMsg}>
          {message}
        </p>
      )}

      <button onClick={() => navigate('/userdashboard')} className={styles.backBtn}>
        Back to Dashboard
      </button>
    </div>
  );
};

export default RaiseRequest;
