import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

const Calculate = () => {
  const navigate = useNavigate();
  const [studentData, setStudentData] = useState(null);
  const [semesters, setSemesters] = useState([]);

  useEffect(() => {
    const form = JSON.parse(localStorage.getItem("studentForm"));
    const counts = JSON.parse(localStorage.getItem("subjectCounts"));

    if (form && counts) {
      setStudentData(form);

      const tempSemesters = counts.map((count, semIndex) => ({
        semesterNumber: semIndex + 1,
        subjects: Array(Number(count))
          .fill()
          .map(() => ({
            subjectName: "",
            grade: "",
            credits: "",
          })),
      }));

      setSemesters(tempSemesters);
    }
  }, []);

  const handleSubjectChange = (semIndex, subjIndex, field, value) => {
    const updated = [...semesters];
    updated[semIndex].subjects[subjIndex][field] = value;
    setSemesters(updated);
  };

  const convertGradeToPoint = (grade) => {
    switch (grade.toUpperCase()) {
      case "O": return 10;
      case "A+": return 9;
      case "A": return 8;
      case "B+": return 7;
      case "B": return 6;
      case "C": return 5;
      case "D": return 4;
      default: return 0;
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    let totalPoints = 0;
    let totalCredits = 0;
    let semesterResults = [];

    semesters.forEach((sem) => {
      let semPoints = 0;
      let semCredits = 0;

      sem.subjects.forEach((subj) => {
        const points = convertGradeToPoint(subj.grade);
        const credits = Number(subj.credits);
        semPoints += points * credits;
        semCredits += credits;
      });

      const semCgpa = semCredits > 0 ? (semPoints / semCredits).toFixed(2) : 0;
      semesterResults.push({ semesterNumber: sem.semesterNumber, cgpa: semCgpa });

      totalPoints += semPoints;
      totalCredits += semCredits;
    });

    const overallCgpa = totalCredits > 0 ? (totalPoints / totalCredits).toFixed(2) : 0;

    // Store in localStorage for Result.jsx
    localStorage.setItem(
      "resultData",
      JSON.stringify({
        student: studentData,
        semesterResults: semesterResults,
        overallCgpa: overallCgpa,
      })
    );

    navigate("/result");
  };

  return (
    <div>
      <h2>Enter Subjects Information</h2>
      <form onSubmit={handleSubmit}>
        {semesters.map((sem, semIndex) => (
          <div key={semIndex} style={{ border: "1px solid gray", margin: "10px", padding: "10px" }}>
            <h3>Semester {sem.semesterNumber}</h3>
            {sem.subjects.map((subj, subjIndex) => (
              <div key={subjIndex} style={{ display: "flex", gap: "10px", marginBottom: "5px" }}>
                <input
                  type="text"
                  placeholder="Subject Name"
                  value={subj.subjectName}
                  onChange={(e) =>
                    handleSubjectChange(semIndex, subjIndex, "subjectName", e.target.value)
                  }
                  required
                />
                <select
                  value={subj.grade}
                  onChange={(e) =>
                    handleSubjectChange(semIndex, subjIndex, "grade", e.target.value)
                  }
                  required
                >
                  <option value="">Select Grade</option>
                  <option value="O">O</option>
                  <option value="A+">A+</option>
                  <option value="A">A</option>
                  <option value="B+">B+</option>
                  <option value="B">B</option>
                  <option value="C">C</option>
                  <option value="D">D</option>
                </select>
                <input
                  type="number"
                  placeholder="Credits"
                  value={subj.credits}
                  onChange={(e) =>
                    handleSubjectChange(semIndex, subjIndex, "credits", e.target.value)
                  }
                  required
                />
              </div>
            ))}
          </div>
        ))}
        <button type="submit">Calculate CGPA</button>
      </form>
    </div>
  );
};

export default Calculate;
