import React, { useState } from "react";
import styles from "./StudentForm.module.css"

const StudentForm = () => {
  const [data, setData] = useState({
    name: "",
    roll: "",
    dept: "",
    semCount: "",
  });
  const handleChange = (e) => {
    setData({ ...data, [e.target.name]: e.target.value });
  };
  const handleSubmit = () => {
    if (!data.name || !data.roll || !data.dept || !data.semCount) {
      return;
    }
    setData({
      name: "",
      roll: "",
      dept: "",
      semCount: "",
    });
    console.log(data);
    
  };
  return (
    <>
      <div class="container">
    <h1>Student Grading System</h1>
    <form id="gradingForm">
      <div class="section">
        <h2>Student Info</h2>
        <input type="text" id="name" placeholder="Student Name" required value={data.name} onChange={handleChange}/>
        <input type="number" id="roll" placeholder="Roll Number" required value={data.roll} onChange={handleChange}/>
        <input type="text" id="dept" placeholder="Department" required value={data.dept} onChange={handleChange}/>
        <input type="number" id="semCount" placeholder="Number of Semesters" value={data.semCount} onChange={handleChange}required />
        <button type="button" onclick="generateSemesters()">Next</button>
      </div>
      <div id="semestersSection"></div>
      <button type="submit">Calculate CGPA</button>
    </form>
    <div id="resultSection"></div>
  </div>
    </>
  );
};

export default StudentForm;
