import React, { useState, useEffect } from 'react';
import axios from 'axios';
import styles from './ManageServices.module.css';

const ManageServices = () => {
  const [newService, setNewService] = useState('');
  const [services, setServices] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  useEffect(() => {
    fetchServices();
  }, []);

  const fetchServices = () => {
    axios.get('http://localhost:8080/services/data')
      .then(response => {
        setServices(response.data);
        setLoading(false);
      })
      .catch(error => {
        setError('Failed to fetch services');
        setLoading(false);
        console.error("Error fetching services:", error);
      });
  };

  const addService = () => {
    if (newService.trim() === '') {
      setError('Service name cannot be empty');
      return;
    }

    const serviceData = {
      serviceName: newService,
      isAvailable: true
    };

    axios.post('http://localhost:8080/services/save', serviceData)
      .then(response => {
        setNewService('');
        setError('');
        setSuccess('Service added successfully!');
        setTimeout(() => setSuccess(''), 3000);
        fetchServices();
      })
      .catch(error => {
        setError(error.response?.data?.message || 'Failed to add service');
        console.error('Error adding service:', error);
      });
  };

  const removeService = (serviceId) => {
    if (window.confirm('Are you sure you want to delete this service?')) {
      axios.delete(`http://localhost:8080/services/delete/${serviceId}`)
        .then(() => {
          setSuccess('Service deleted successfully!');
          setTimeout(() => setSuccess(''), 3000);
          fetchServices();
        })
        .catch(error => {
          setError(error.response?.data?.message || 'Failed to delete service');
          console.error('Error deleting service:', error);
        });
    }
  };

  if (loading) return <div className={styles.loading}>Loading services...</div>;

  return (
    <div className={styles.container}>
      <h2>Manage Services</h2>
      
      {error && <div className={styles.error}>{error}</div>}
      {success && <div className={styles.success}>{success}</div>}
      
      <div className={styles.addService}>
        <input
          type="text"
          placeholder="Enter service name"
          value={newService}
          onChange={(e) => setNewService(e.target.value)}
          required
        />
        <button onClick={addService}>Add Service</button>
      </div>

      <div className={styles.serviceList}>
        {services.map(service => (
          <div key={service.serviceId} className={styles.serviceItem}>
            <span>{service.serviceName}</span>
            <span className={service.isAvailable ? styles.available : styles.unavailable}>
              {service.isAvailable ? 'Available' : 'Unavailable'}
            </span>
            <button 
              onClick={() => removeService(service.serviceId)}
              className={styles.deleteBtn}
            >
              Delete
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ManageServices;
