import React, { useState } from 'react'

const Form = () => {
    const [input,setInput]=useState({
        name:"",
        age:"",
        gender:"",
        department:""
    })
    const handleChange =(e) =>{
        setInput({...input,[e.target.name]:e.target.value})

    }
    const handleSubmit =() =>{
        console.log(input);
        
    }
  return (
    <>
    <h1>Registration Form</h1>
    <form>
        <label>Name</label>
        <input type="text" name="name" id="name" placeholder="Enter your name" value={input.name} onChange={handleChange}/><br />
        <label htmlFor="">Age</label>
        <input type="number" name="age" id="age" placeholder="Enter your age" value={input.age} onChange={handleChange}/><br />
        <label htmlFor="">Gender</label>
        
        <input type="radio" name="male" id="male" value={input.gender} onChange={handleChange}/>
        <label htmlFor="male">Male</label>
      
        <input type="radio" name="female" id="female" value={input.gender} onChange={handleChange}/>
        <label htmlFor="female">Female</label><br />
        <label htmlFor="">Department</label>
        <input type="text" name="department" id="department" placeholder="Enter Departement" value={input.department} onChange={handleChange} /><br />

        <button type="submit">Submit</button>
    </form>
    </>
  )
}

export default Form
