import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styles from './RaiseRequest.module.css';
import axios from 'axios';

const RaiseRequest = () => {
  const [service, setService] = useState('');
  const [description, setDescription] = useState('');
  const [message, setMessage] = useState('');
  const [isError, setIsError] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const navigate = useNavigate();

  // Map service names to service IDs
  const serviceMap = {
    Plumbing: 1,
    Electricity: 2,
    Garbage: 3,
    Maintenance: 4,
    Other: 5,
  };

  // Format date to yyyy-MM-dd
  const formatDate = (date) => date.toISOString().split('T')[0];

  const handleSubmit = () => {
    if (!service || !description) {
      setMessage('Please fill all fields');
      setIsError(true);
      setTimeout(() => setMessage(''), 3000);
      return;
    }

    const newRequest = {
      serviceId: serviceMap[service],
      userId: 1, // You can replace this with actual user ID from login/session
      description: description,
      createDate: formatDate(new Date()),
      exceptCompletionDate: formatDate(new Date(Date.now() + 3 * 24 * 60 * 60 * 1000)), // 3 days later
    };

    setIsSubmitting(true);

    axios.post('http://localhost:8080/service/add', newRequest)
      .then(() => {
        setService('');
        setDescription('');
        setIsError(false);
        setMessage('Request submitted successfully!');
        setTimeout(() => setMessage(''), 3000);
      })
      .catch((error) => {
        setIsError(true);
        setMessage('Error submitting request');
        console.error("Submit Error:", error);
      })
      .finally(() => {
        setIsSubmitting(false);
      });
  };

  return (
    <div className={styles.requestForm}>
      <h3>Raise Request</h3>

      <select
        value={service}
        onChange={(e) => setService(e.target.value)}
        className={styles.input}
      >
        <option value="">Select Service</option>
        <option value="Plumbing">Plumbing</option>
        <option value="Electricity">Electricity</option>
        <option value="Garbage">Garbage Collection</option>
        <option value="Maintenance">Maintenance</option>
        <option value="Other">Other</option>
      </select>

      <br />

      <textarea
        name="description"
        placeholder="Description"
        value={description}
        onChange={(e) => setDescription(e.target.value)}
        className={styles.textarea}
      />

      <br />

      <button
        onClick={handleSubmit}
        className={styles.submitButton}
        disabled={isSubmitting}
      >
        {isSubmitting ? 'Submitting...' : 'Submit'}
      </button>

      {message && (
        <p className={isError ? styles.errorMsg : styles.successMsg}>
          {message}
        </p>
      )}

      <br />

      <button
        onClick={() => navigate('/userdashboard')}
        className={styles.backBtn}
      >
        Back to Dashboard
      </button>
    </div>
  );
};

export default RaiseRequest;
