import React, { useState, useEffect } from 'react';
import axios from 'axios';
import styles from './ManageServices.module.css';

const ManageServices = () => {
  const [services, setServices] = useState([]);
  const [newService, setNewService] = useState('');

  // Fetch all services on component mount
  useEffect(() => {
    axios.get("http://localhost:8080/services/data")
      .then((response) => {
        setServices(response.data || []);
      })
      .catch((error) => {
        console.error("Error fetching services:", error);
      });
  }, []);

  // Add a new service
  const addService = () => {
    if (!newService.trim()) return;

    axios.post('http://localhost:8080/services/save', {
      serviceName: newService,
      isAvailable: true
    })
    .then((response) => {
      setServices(prev => [...prev, response.data]);
      setNewService('');
      alert('Service added successfully');
    })
    .catch((err) => {
      console.error('Error adding service:', err);
      alert('Failed to add service');
    });
  };

  // Delete a service by ID
  const removeService = (serviceId) => {
    if (window.confirm('Are you sure you want to delete this service?')) {
      axios.delete(`http://localhost:8080/services/delete/${serviceId}`)
        .then(() => {
          setServices(services.filter(service => service.serviceId !== serviceId));
        })
        .catch((err) => {
          console.error('Error deleting service:', err);
          alert('Failed to delete service');
        });
    }
  };

  return (
    <div className={styles.container}>
      <h2>Manage Services</h2>

      <div className={styles.addService}>
        <input
          type="text"
          value={newService}
          onChange={(e) => setNewService(e.target.value)}
          placeholder="Enter new service name"
        />
        <button onClick={addService}>Add Service</button>
      </div>

      <table border={2} className={styles.serviceTable}>
        <thead>
          <tr>
            <th>ID</th>
            <th>Service Name</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {services.length === 0 ? (
            <tr>
              <td colSpan="4">No services available.</td>
            </tr>
          ) : (
            services.map((service) => (
              <tr key={service.serviceId}>
                <td>{service.serviceId}</td>
                <td>{service.serviceName}</td>
                <td>{service.isAvailable ? 'Available' : 'Unavailable'}</td>
                <td>
                  <button
                    onClick={() => removeService(service.serviceId)}
                    className={styles.deleteButton}
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
};

export default ManageServices;
