import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styles from './RaiseRequest.module.css';
import axios from 'axios';

const RaiseRequest = () => {
  const [serviceId, setServiceId] = useState('');
  const [description, setDescription] = useState('');
  const [message, setMessage] = useState('');
  const [isError, setIsError] = useState(false);
  const navigate = useNavigate();
  const userId = parseInt(localStorage.getItem('userId'));

  const handleSubmit = () => {
    if (!serviceId || !description) {
      setMessage('Please fill all fields');
      setIsError(true);
      setTimeout(() => setMessage(''), 3000);
      return;
    }

    const newRequest = {
      serviceId: parseInt(serviceId),
      userId: userId,
      description: description,
      status: "Pending",
      createDate: new Date().toISOString(),
      exceptCompletionDate: new Date(Date.now() + 86400000).toISOString() // +1 day
    };

    axios.post(`http://localhost:8080/requests/save`, newRequest)
      .then(response => {
        setServiceId('');
        setDescription('');
        setIsError(false);
        setMessage('Request submitted successfully! Admin will review it soon.');
        setTimeout(() => setMessage(''), 3000);
      })
      .catch(error => {
        console.error("Error submitting request:", error);
        setMessage('Failed to submit request. Please try again.');
        setIsError(true);
        setTimeout(() => setMessage(''), 3000);
      });
  };

  return (
    <div className={styles.requestForm}>
      <h3>Raise Service Request</h3>
      <select
        value={serviceId}
        onChange={(e) => setServiceId(e.target.value)}
        className={styles.input}
        required
      >
        <option value="">Select Service</option>
        <option value="1">Plumbing</option>
        <option value="2">Electricity</option>
        <option value="3">Garbage Collection</option>
        <option value="4">Maintenance</option>
      </select>
      <textarea
        placeholder="Describe your issue in detail..."
        value={description}
        onChange={(e) => setDescription(e.target.value)}
        className={styles.textarea}
        required
      />
      <button onClick={handleSubmit} className={styles.submitButton}>
        Submit Request
      </button>
      {message && (
        <p className={isError ? styles.errorMsg : styles.successMsg}>
          {message}
        </p>
      )}
      <button onClick={() => navigate('/userdashboard')} className={styles.backBtn}>
        Back to Dashboard
      </button>
    </div>
  );
};

export default RaiseRequest;
