import React, { useEffect, useState, useRef } from "react";
import html2canvas from "html2canvas";

const Result = () => {
  const [data, setData] = useState(null);
  const resultRef = useRef();

  useEffect(() => {
    const stored = JSON.parse(localStorage.getItem("resultData"));
    if (stored) {
      setData(stored);
    }
  }, []);

  const downloadImage = () => {
    html2canvas(resultRef.current).then((canvas) => {
      const link = document.createElement("a");
      link.download = "CGPA_Result.png";
      link.href = canvas.toDataURL("image/png");
      link.click();
    });
  };

  if (!data) return <h2>No result data found</h2>;

  return (
    <div>
      <div ref={resultRef} style={{ padding: "20px", border: "1px solid gray", width: "fit-content" }}>
        <h2>Student CGPA Report</h2>
        <p><strong>Name:</strong> {data.student.name}</p>
        <p><strong>Roll Number:</strong> {data.student.roll}</p>
        <p><strong>Department:</strong> {data.student.dept}</p>

        <h3>Semester-wise CGPA</h3>
        <ul>
          {data.semesterResults.map((sem) => (
            <li key={sem.semesterNumber}>
              Semester {sem.semesterNumber}: {sem.cgpa}
            </li>
          ))}
        </ul>

        <h3>Overall CGPA: {data.overallCgpa}</h3>
      </div>

      <button onClick={downloadImage} style={{ marginTop: "20px" }}>
        Download as Image
      </button>
    </div>
  );
};

export default Result;
