import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styles from './StudentForm.module.css'; // Import CSS Module

const StudentForm = () => {
  const navigate = useNavigate();
  const [form, setForm] = useState({
    name: '',
    roll: '',
    dept: '',
    semCount: ''
  });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    localStorage.setItem('studentForm', JSON.stringify(form));
    setForm({
      name: '',
      roll: '',
      dept: '',
      semCount: ''
    });
    navigate('/subject');
  };

  return (
     <div><h1 className={styles.heading}>Student Grading System</h1> 
    <div className={styles.container}>
         
      <h2 className={styles.heading}>Student Information</h2>
      <form onSubmit={handleSubmit} className={styles.form}>
        <input
          className={styles.input}
          type="text"
          name="name"
          placeholder="Student Name"
          value={form.name}
          onChange={handleChange}
          required
        />
        <input
          className={styles.input}
          type="number"
          name="roll"
          placeholder="Register Number"
          value={form.roll}
          onChange={handleChange}
          required
        />
        <input
          className={styles.input}
          type="text"
          name="dept"
          placeholder="Department"
          value={form.dept}
          onChange={handleChange}
          required
        />
        <input
          className={styles.input}
          type="number"
          name="semCount"
          placeholder="No of Semesters"
          value={form.semCount}
          onChange={handleChange}
          required
        />
        <button type="submit" className={styles.button}>Next</button>
      </form>
    </div>
    </div>
  );
};

export default StudentForm;
