import React, { useState } from 'react';
import styles from './Login.module.css';

const Login = () => {
  const [form, setForm] = useState({
     name: "",
    password: "",
    role: ""
  });
  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Login Details:', { username, password, role });
    alert("Form submitted successfully!");
    setForm({
      name: "",
      password: "",
      role: ""
    })
  };

  return (
    <>
      <h1 className={styles.head}>My Colony</h1>
      <form className={styles.login} onSubmit={handleSubmit}>
        <h1>Login</h1>
        <input type="text" placeholder="Username" value={form.name} onChange={handleChange} required />
        <input type="password" placeholder="Password" value={form.password} onChange={handleChange} required />
        <select value={form.role} onChange={handleChange} required>
          <option value="">Select Role</option>
          <option value="Admin">Admin</option>
          <option value="Super Admin">Super Admin</option>
          <option value="User">User</option>
        </select>
        <a href="register">Forgot password?</a>
        <button type="submit">Log in</button>
        <a href="new">Create new account</a>
      </form>
    </>
  );
};

export default Login;
