import React, { useState } from 'react';

const ToDo = () => {
  const [task, setTask] = useState('');  // For input
  const [list, setList] = useState([]); // For tasks array

  const handleChange = (e) => {
    setTask(e.target.value);
  };

  const handleClick = () => {
    if (task.trim() !== '') {
      setList([...list, task]); // Add new task
      setTask(''); // Clear input
    }
  };

  return (
    <>
      <div>
        <h1>To Do List</h1>
        <textarea
          name="list"
          id="list"
          value={task}
          onChange={handleChange}
          placeholder="Enter your task"
        />
        <button onClick={handleClick}>Add</button>
      </div>

      <ul>
        {list.map((item, index) => (
          <li key={index}>{item}</li>
        ))}
      </ul>
    </>
  );
};

export default ToDo;
