import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import styles from './Apis.module.css';

const Apis = () => {
  const navigate = useNavigate();
  const [input, setInput] = useState([]);

  const handleSubmit = () => {
    axios.get("http://localhost:8080/student/data").then((response) => {
        console.log(response.data);
        setInput(response.data);
      });
  };

  useEffect(() => {
    handleSubmit();
  }, []);

  const handleEdit = (id) => {
    console.log("Edit", id);
    axios.get(`http://localhost:8080/student/get/${id}`).then((response) => {
        navigate("/edit", { state: { student: response.data } });
      });
  };

  return (
    <>
     <h1 style={{textAlign:''}}>Student Information</h1>
    <div className={styles.tableContainer}>
     
      <table className={styles.table}>
        <thead>
          <tr>
            <th>Id</th>
            <th>Name</th>
            <th>Department</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {input.map((item, index) => (
            <tr key={index}>
              <td>{item.id}</td>
              <td>{item.name}</td>
              <td>{item.dept}</td>
              <td>
                <button className={styles.editButton} onClick={() => handleEdit(item.id)}>
                  Edit
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
    </>
  );
};

export default Apis;
