import React, { useEffect, useState } from 'react';
import styles from './ManageRequest.module.css';
import axios from 'axios';

const ManageRequest = () => {
  const [requests, setRequests] = useState([]);

  // Fetch all service requests
  const fetchRequests = () => {
    axios
      .get('http://localhost:8080/service/data')
      .then((res) => {
        setRequests(res.data);
      })
      .catch((err) => {
        console.error('Error fetching requests:', err);
      });
  };

  // Load data on page load
  useEffect(() => {
    fetchRequests();
  }, []);

  // Update request status
  const handleStatusUpdate = (id) => {
    axios
      .put(`http://localhost:8080/service/update/${id}`, { status: 'Completed' })
      .then(() => {
        fetchRequests(); // refresh list after update
      })
      .catch((err) => {
        console.error('Error updating status:', err);
      });
  };

  return (
    <div className={styles.container}>
      <h2 className={styles.heading}>Manage Service Requests</h2>

      {requests.length === 0 ? (
        <p>No requests found.</p>
      ) : (
        requests.map((req) => (
          <div key={req.requestId} className={styles.card}>
            <h3>Request ID: {req.requestId}</h3>
            <p><strong>User ID:</strong> {req.userId}</p>
            <p><strong>Service ID:</strong> {req.serviceId}</p>
            <p><strong>Description:</strong> {req.description}</p>
            <p><strong>Status:</strong> {req.status}</p>
            <p><strong>Created:</strong> {req.createDate}</p>
            <p><strong>Expected Completion:</strong> {req.exceptCompletionDate}</p>
            {req.status !== 'Completed' && (
              <button className={styles.button} onClick={() => handleStatusUpdate(req.requestId)}>
                Mark as Completed
              </button>
            )}
          </div>
        ))
      )}
    </div>
  );
};

export default ManageRequest;
