import React, { useState } from 'react';
import axios from 'axios';
import styles from './Forgot.module.css';

const Forgot = () => {
  const [email, setEmail] = useState("");
  const [newPassword, setNewPassword] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!email || !newPassword) {
      alert("Both email and new password are required");
      return;
    }

    try {
      const res = await axios.put("http://localhost:8080/forgot-password", {
        email,
        newPassword
      });

      alert(res.data);
      setEmail("");
      setNewPassword("");
    } catch (err) {
      alert(err.response?.data || "Error updating password");
    }
  };

  return (
    <div className={styles.forgotContainer}>
      <h2>Reset Password</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="email"
          name="email"
          placeholder="Enter email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          className={styles.forgotInput}
          required
        />
        <input
          type="password"
          name="newPassword"
          placeholder="Enter new password"
          value={newPassword}
          onChange={(e) => setNewPassword(e.target.value)}
          className={styles.forgotInput}
          required
        />
        <button type="submit" className={styles.forgotButton}>Submit</button>
      </form>
    </div>
  );
};

export default Forgot;
