import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import styles from './RaiseRequest.module.css';

const RaiseRequest = () => {
  const [serviceId, setServiceId] = useState('');
  const [description, setDescription] = useState('');
  const [message, setMessage] = useState('');
  const [isError, setIsError] = useState(false);
  const [services, setServices] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();
  const userId = localStorage.getItem('userId');

  useEffect(() => {
    axios.get('http://localhost:8080/services/data')
      .then(response => {
        // Filter only available services
        const availableServices = response.data.filter(service => service.isAvailable);
        setServices(availableServices);
        setLoading(false);
      })
      .catch(error => {
        console.error('Error fetching services:', error);
        setMessage('Failed to load services');
        setIsError(true);
        setLoading(false);
      });
  }, []);

  const handleSubmit = () => {
    if (!serviceId || !description) {
      setMessage('Please fill all fields');
      setIsError(true);
      setTimeout(() => setMessage(''), 3000);
      return;
    }

    const newRequest = {
      serviceId: parseInt(serviceId),
      userId: parseInt(userId),
      description: description,
      status: "Pending",
      createDate: new Date().toISOString(),
      exceptCompletionDate: new Date(Date.now() + 86400000).toISOString()
    };

    axios.post('http://localhost:8080/requests/save', newRequest)
      .then(response => {
        setServiceId('');
        setDescription('');
        setIsError(false);
        setMessage('Request submitted successfully!');
        setTimeout(() => {
          setMessage('');
          navigate('/userdashboard');
        }, 2000);
      })
      .catch(error => {
        console.error("Error submitting request:", error);
        setMessage(error.response?.data?.message || 'Something went wrong. Try again.');
        setIsError(true);
        setTimeout(() => setMessage(''), 3000);
      });
  };

  if (loading) return <div className={styles.loading}>Loading services...</div>;

  return (
    <div className={styles.container}>
      <h2>Raise New Request</h2>
      
      {message && (
        <div className={isError ? styles.error : styles.success}>
          {message}
        </div>
      )}

      <div className={styles.formGroup}>
        <label>Select Service:</label>
        <select
          value={serviceId}
          onChange={(e) => setServiceId(e.target.value)}
          required
        >
          <option value="">-- Select a service --</option>
          {services.map(service => (
            <option key={service.serviceId} value={service.serviceId}>
              {service.serviceName}
            </option>
          ))}
        </select>
      </div>

      <div className={styles.formGroup}>
        <label>Description:</label>
        <textarea
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          placeholder="Describe your request..."
          required
        />
      </div>

      <div className={styles.buttonGroup}>
        <button onClick={handleSubmit}>Submit Request</button>
        <button onClick={() => navigate('/userdashboard')}>Cancel</button>
      </div>
    </div>
  );
};

export default RaiseRequest;
