import React, { useEffect, useState } from 'react'
import { useLocation, useNavigate } from 'react-router-dom'
const EditForm = () => {
    const navigate = useNavigate();
    const location = useLocation();
    const student = location.state?.student;
    console.log(student);
    const [form,setForm] = useState({
        name:'',
        dept:''
    })
    useEffect(()=>{
        if (student) {
            setForm({
                name:student.name,
                dept:student.dept
            })
        }
    },[student])
    const handleChange =(e)=>{
        setForm({...form,[e.target.name]:e.target.value})
    }
    const handleUpdate = (e) => {
    e.preventDefault();
    axios.put(`http://localhost:8080/update/${student}`, student)
      .then(response => {
        setMessage("Student updated successfully!");
        console.log("Updated:", response.data);
      })
      .catch(error => {
        setMessage("Update failed. Check console.");
        console.error("Update error:", error);
      });
  };
    
  return (
    <>
    <form >
        <input type="name" name='name' value={form.name}onChange={handleChange}/>
        <input type="name" name='dept'value={form.dept}onChange={handleChange}/>
        <button onClick={()=>handleUpdate}>Update</button>
    </form>
    </>
  )
}

export default EditForm
