import React, { useEffect, useState } from 'react';
import styles from './ManageRequest.module.css';

const ManageRequest = () => {
  const [requests, setRequests] = useState([]);

  useEffect(() => {
    fetch('http://localhost:8080/api/requests')
      .then(response => response.json())
      .then(data => setRequests(data))
      .catch(error => console.error('Error fetching requests:', error));
  }, []);

  const handleStatusUpdate = (id, newStatus) => {
    fetch(`http://localhost:8080/api/requests/${id}/status?status=${newStatus}`, {
      method: 'PUT'
    })
    .then(response => response.json())
    .then(updatedRequest => {
      setRequests(requests.map(req => 
        req.requestId === id ? updatedRequest : req
      ));
    })
    .catch(error => console.error('Error updating status:', error));
  };

  return (
    <div className={styles.container}>
      <h2 className={styles.heading}>Manage Requests</h2>
      {requests.map((req) => (
        <div key={req.requestId} className={styles.card}>
          <h3>User ID: {req.userId}</h3>
          <p><strong>Service ID:</strong> {req.serviceId}</p>
          <p><strong>Description:</strong> {req.description}</p>
          <p><strong>Status:</strong> {req.status}</p>
          <button className={styles.button} onClick={() => handleStatusUpdate(req.requestId, 'Completed')}>
            Mark as Completed
          </button>
        </div>
      ))}
    </div>
  );
};

export default ManageRequest;
