import React, { useEffect, useState } from 'react';
import styles from './MyRequests.module.css';
import RequestDetail from './RequestDetail';

const MyRequests = ({ userId }) => {
  const [requests, setRequests] = useState([]);
  const [selectedRequest, setSelectedRequest] = useState(null);

  useEffect(() => {
    fetch(`http://localhost:8080/api/requests/user/${userId}`)
      .then(response => response.json())
      .then(data => setRequests(data))
      .catch(error => console.error('Error fetching requests:', error));
  }, [userId]);

  return (
    <div className={styles.container}>
      <h2>My Service Requests</h2>
      <ul className={styles.requestList}>
        {requests.map((req) => (
          <li key={req.requestId} className={styles.requestItem} onClick={() => setSelectedRequest(req)}>
            <div>{req.serviceId}</div>
            <div className={styles.status}>{req.status}</div>
          </li>
        ))}
      </ul>

      {selectedRequest && (
        <RequestDetail request={selectedRequest} onClose={() => setSelectedRequest(null)} />
      )}
    </div>
  );
};

export default MyRequests;
