import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import styles from './SuperAdminDashboard.module.css';

const SuperAdminDashboard = () => {
  const [stats, setStats] = useState({
    totalRequests: 0,
    completedRequests: 0,
    pendingRequests: 0,
    totalResidents: 0
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const [requestsRes, usersRes] = await Promise.all([
          axios.get('http://localhost:8080/requests/data'),
          axios.get('http://localhost:8080/users/data')
        ]);

        const requests = requestsRes.data;
        const users = usersRes.data;

        setStats({
          totalRequests: requests.length,
          completedRequests: requests.filter(r => r.status === 'Completed').length,
          pendingRequests: requests.filter(r => r.status === 'Pending').length,
          totalResidents: users.filter(u => u.role === 'User').length
        });
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };
    fetchStats();
  }, []);

  const handleLogout = () => {
    localStorage.clear();
    navigate('/login');
  };

  if (loading) return <div className={styles.container}>Loading dashboard...</div>;
  if (error) return <div className={styles.container}>Error: {error}</div>;

  return (
    <div className={styles.container}>
      <h2>Super Admin Dashboard</h2>
      <div className={styles.cards}>
        <div className={styles.card}>
          <h4>Total Requests</h4>
          <p>{stats.totalRequests}</p>
        </div>
        <div className={styles.card}>
          <h4>Completed</h4>
          <p>{stats.completedRequests}</p>
        </div>
        <div className={styles.card}>
          <h4>Pending</h4>
          <p>{stats.pendingRequests}</p>
        </div>
        <div className={styles.card}>
          <h4>Total Residents</h4>
          <p>{stats.totalResidents}</p>
        </div>
      </div>

      <div className={styles.actions}>
        <Link to="/reports" className={styles.reportsButton}>
          View Detailed Reports
        </Link>
        <button onClick={handleLogout} className={styles.logoutButton}>
          Logout
        </button>
      </div>
    </div>
  );
};

export default SuperAdminDashboard;
