import React, { useEffect, useState } from 'react';
import { useLocation, useParams, useNavigate } from 'react-router-dom';
import styles from './RequestDetail.module.css';
import axios from 'axios';

const RequestDetail = () => {
  const [request, setRequest] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { id } = useParams();
  const location = useLocation();
  const navigate = useNavigate();

  useEffect(() => {
    const fetchRequest = () => {
      axios.get(`http://localhost:8080/requests/${id}`)
        .then(response => {
          setRequest(response.data);
          setLoading(false);
        })
        .catch(error => {
          console.error('Error fetching request:', error);
          setError(error.message);
          setLoading(false);
        });
    };

    if (location.state?.request) {
      setRequest(location.state.request);
      setLoading(false);
    } else if (id) {
      fetchRequest();
    } else {
      setLoading(false);
    }
  }, [id, location.state]);

  const handleEdit = () => {
    navigate(`/requests/edit/${id}`, { state: { request } }); // Changed path to be more RESTful
  };

  const handleDelete = () => {
    if (window.confirm('Are you sure you want to delete this request?')) {
      axios.delete(`http://localhost:8080/requests/delete/${id}`)
        .then(() => {
          alert('Request deleted successfully');
          navigate('/viewrequests', { replace: true }); // Added replace to prevent back navigation to deleted item
        })
        .catch(error => {
          console.error('Error deleting request:', error);
          alert(`Failed to delete request: ${error.response?.data?.message || error.message}`);
        });
    }
  };

  const handleBack = () => {
    navigate('/requests'); // Changed to always go to requests list rather than previous page
  };

  const formatDate = (dateString) => {
    if (!dateString) return 'Not specified';
    
    try {
      const date = new Date(dateString);
      return date.toLocaleString('en-US', {
        year: 'numeric',
        month: 'long',
        day: 'numeric',
        hour: '2-digit',
        minute: '2-digit'
      });
    } catch (e) {
      console.error('Error formatting date:', e);
      return dateString; 
    }
  };

  if (loading) return <div className={styles.detailContainer}>Loading...</div>;
  if (error) return <div className={styles.detailContainer}>Error: {error}</div>;
  if (!request) return <div className={styles.detailContainer}>No request found</div>;

  return (
    <div className={styles.detailContainer}>
      <h3>Request Details</h3>
      <div className={styles.detailGrid}>
        {/* ... (keep your existing detail items) ... */}
        <div className={styles.buttonGroup}>
          <button 
            onClick={handleEdit}
            className={styles.editButton}
            disabled={!request} // Disable if no request data
          >
            Edit
          </button>
          <button 
            onClick={handleDelete}
            className={styles.deleteButton}
            disabled={!request} // Disable if no request data
          >
            Delete
          </button>
          <button 
            onClick={handleBack}
            className={styles.backButton}
          >
            Back to Requests
          </button>
        </div>
      </div>
    </div>
  );
};

export default RequestDetail;
