import React, { useState, useEffect } from 'react';
import styles from './ManageRequest.module.css';
import axios from 'axios';

const ManageRequest = () => {
  const [requests, setRequests] = useState([]);

  useEffect(() => {
    fetchRequests();
    const interval = setInterval(fetchRequests, 10000); // Refresh every 10 seconds
    return () => clearInterval(interval);
  }, []);

  const fetchRequests = () => {
    axios.get('http://localhost:8080/request/data')
      .then((response) => {
        setRequests(response.data);
      })
      .catch((error) => {
        console.error("Error fetching requests:", error);
      });
  };

  const handleStatusUpdate = (id, newStatus) => {
    axios.put(`http://localhost:8080/service/update/${id}`, { status: newStatus })
      .then(() => {
        fetchRequests();
      })
      .catch((error) => {
        console.error("Error updating request status:", error);
      });
  };

  return (
    <div className={styles.container}>
      <h2 className={styles.heading}>Manage Requests</h2>
      {requests.length === 0 ? (
        <p>No requests available.</p>
      ) : (
        requests.map((req) => (
          <div key={req.requestId} className={styles.card}>
            <h3>User: {req.userName}</h3>
            <p><strong>Service:</strong> {req.serviceName}</p>
            <p><strong>Description:</strong> {req.description}</p>
            <p><strong>Status:</strong> {req.status}</p>
            <p><strong>Raised on:</strong> {req.dateTime}</p>
            {req.status !== 'Completed' && (
              <button className={styles.button} onClick={() => handleStatusUpdate(req.requestId, 'Completed')}>
                Mark as Completed
              </button>
            )}
          </div>
        ))
      )}
    </div>
  );
};

export default ManageRequest;
