import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import styles from './RaiseRequest.module.css';

const RaiseRequest = () => {
  const [service, setService] = useState('');
  const [description, setDescription] = useState('');
  const [message, setMessage] = useState('');
  const [isError, setIsError] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = () => {
    if (!service || !description) {
      setMessage('Please fill all fields');
      setIsError(true);
      setTimeout(() => setMessage(''), 3000);
      return;
    }

    const newRequest = {
      serviceId: service === 'Plumbing' ? 1 : 2, // Adjust based on actual serviceId mapping
      userId: 1, // Replace with actual logged-in user ID
      description: description,
      status: 'Pending',
      createDate: new Date().toISOString(), // Consider using LocalDate in backend for consistency
      exceptCompletionDate: '' // You can compute a future date if needed
    };

    axios.post('http://localhost:8080/request/save', newRequest)
      .then((response) => {
        setMessage('Request submitted successfully!');
        setIsError(false);
        setService('');
        setDescription('');
        setTimeout(() => {
          setMessage('');
          navigate('/userdashboard');
        }, 2000);
      })
      .catch((error) => {
        console.error(error);
        setMessage('Failed to submit request.');
        setIsError(true);
      });
  };

  return (
    <div className={styles.requestForm}>
      <h3>Raise Request</h3>

      <select
        value={service}
        onChange={(e) => setService(e.target.value)}
        className={styles.input}
      >
        <option value="">Select Service</option>
        <option value="Plumbing">Plumbing</option>
        <option value="Electricity">Electricity</option>
      </select>

      <textarea
        placeholder="Description"
        value={description}
        onChange={(e) => setDescription(e.target.value)}
        className={styles.textarea}
      />

      <button onClick={handleSubmit} className={styles.submitButton}>
        Submit
      </button>

      {message && (
        <p className={isError ? styles.errorMsg : styles.successMsg}>
          {message}
        </p>
      )}

      <button
        onClick={() => navigate('/userdashboard')}
        className={styles.backBtn}
      >
        Back to Dashboard
      </button>
    </div>
  );
};

export default RaiseRequest;
