import React, { useEffect, useState } from 'react'
import axios from 'axios'
import { useNavigate } from 'react-router-dom'
const Apis = () => {
    const navigate = useNavigate();
    const [input,setInput] = useState([])
    const handleSubmit =()=>{
        axios.get("http://localhost:8080/student/data").then((Response)=>{
            console.log(Response.data);
            
            setInput(Response.data);
        })
    }
    useEffect(()=>{
        handleSubmit()   
    },[])    
    const handleEdit =(id) =>{
        console.log("Edit",id);
        axios.get(`http://localhost:8080/student/get/${id}`).then((Response) =>{
            navigate("/edit",{state:{student:Response.data}});
        })
        
    }
  return (
    <>
    <div style={{
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    height: '100vh' // full screen height
  }}>
    <table border={2} >
        <thead>
            <tr>
                <th>Id</th>
                <th>Name</th>
                <th>Department</th>
                <th>Action</th>
            </tr>
        </thead>
        <tbody>
            {input.map((item,index)=>{
                return(
                    <tr key={index}>
                    
                        <td>{item.id}</td>
                        <td>{item.name}</td>
                        <td>{item.dept}</td>
                        <td><button onClick={()=>handleEdit(item.id)} title='Edit'>Edit</button></td>
                    </tr>
                )
            })}
        </tbody>
    </table>
    </div>
    </>
  )
}

export default Apis
