import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import styles from './RequestDetail.module.css';

const RequestDetail = () => {
  const location = useLocation();
  const { request } = location.state || {};
  const [status, setStatus] = useState(request?.status);
  const [notification, setNotification] = useState('');

  // Simulate a status change notification (e.g., after 5 seconds)
  useEffect(() => {
    const timer = setTimeout(() => {
      const newStatus = 'Completed'; // Example change
      if (newStatus !== status) {
        setStatus(newStatus);
        setNotification(`Status changed to ${newStatus}`);
      }
    }, 5000);

    return () => clearTimeout(timer);
  }, [status]);

  if (!request) return <p>No request details found.</p>;

  return (
    <div className={styles.detailContainer}>
      <h3>Request Detail</h3>
      <p><strong>Service:</strong> {request.service}</p>
      <p><strong>Description:</strong> {request.description}</p>
      <p><strong>Date/Time:</strong> {request.dateTime || "Not Available"}</p>
      <p><strong>Status:</strong> {status}</p>
      {notification && <p className={styles.notification}>{notification}</p>}
    </div>
  );
};

export default RequestDetail;
