import React, { useState, useEffect, useCallback } from 'react';
import styles from './ManageUsers.module.css';
import axios from 'axios';

const ManageUsers = () => {
  const [users, setUsers] = useState([]);
  const [newUser, setNewUser] = useState({
    userName: '',
    email: '',
    mobileNo: '',
    block: '',
    floor: '',
    doorNo: '',
    role: 'User',
    password: ''
  });
  const [editingUser, setEditingUser] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [message, setMessage] = useState({ text: '', isError: false });

  const showMessage = useCallback((text, isError = false) => {
    setMessage({ text, isError });
    setTimeout(() => setMessage({ text: '', isError: false }), 3000);
  }, []);

  const fetchUsers = useCallback(() => {
    axios.get('http://localhost:8080/users/data')
      .then(response => setUsers(response.data))
      .catch(error => {
        console.error('Error fetching users:', error);
        showMessage('Failed to load users', true);
      });
  }, [showMessage]);

  useEffect(() => {
    fetchUsers();
  }, [fetchUsers]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    if (editingUser) {
      setEditingUser({ ...editingUser, [name]: value });
    } else {
      setNewUser({ ...newUser, [name]: value });
    }
  };

  const handleAddUser = (e) => {
    e.preventDefault();
    const payload = {
      ...newUser,
      mobileNo: parseInt(newUser.mobileNo),
      floor: parseInt(newUser.floor),
      doorNo: parseInt(newUser.doorNo)
    };

    axios.post('http://localhost:8080/users/save', payload)
      .then(response => {
        setUsers([...users, response.data]);
        resetForm();
        showMessage('User added successfully');
      })
      .catch(error => {
        console.error('Error adding user:', error);
        showMessage('Failed to add user', true);
      });
  };

  const handleUpdateUser = (e) => {
    e.preventDefault();
    const payload = {
      ...editingUser,
      mobileNo: parseInt(editingUser.mobileNo),
      floor: parseInt(editingUser.floor),
      doorNo: parseInt(editingUser.doorNo)
    };

    axios.put(`http://localhost:8080/users/update/${editingUser.userId}`, payload)
      .then(response => {
        setUsers(users.map(user => 
          user.userId === editingUser.userId ? response.data : user
        ));
        setEditingUser(null);
        showMessage('User updated successfully');
      })
      .catch(error => {
        console.error('Error updating user:', error);
        showMessage('Failed to update user', true);
      });
  };

  const handleDeleteUser = (userId) => {
    if (window.confirm('Are you sure you want to delete this user?')) {
      axios.delete(`http://localhost:8080/users/delete/${userId}`)
        .then(() => {
          setUsers(users.filter(user => user.userId !== userId));
          showMessage('User deleted successfully');
        })
        .catch(error => {
          console.error('Error deleting user:', error);
          showMessage('Failed to delete user', true);
        });
    }
  };

  const handleEditUser = (user) => {
    setEditingUser({ ...user });
  };

  const cancelEdit = () => {
    setEditingUser(null);
  };

  const resetForm = () => {
    setNewUser({
      userName: '',
      email: '',
      mobileNo: '',
      block: '',
      floor: '',
      doorNo: '',
      role: 'User',
      password: ''
    });
  };

  const filteredUsers = users.filter(user =>
    user.userName.toLowerCase().includes(searchTerm.toLowerCase()) ||
    user.email.toLowerCase().includes(searchTerm.toLowerCase()) ||
    user.role.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className={styles.container}>
      <h2>Manage Users</h2>
      
      {message.text && (
        <div className={message.isError ? styles.errorMessage : styles.successMessage}>
          {message.text}
        </div>
      )}

      <div className={styles.searchContainer}>
        <input
          type="text"
          placeholder="Search users..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className={styles.searchInput}
        />
      </div>

      <div className={styles.userTableContainer}>
        <table className={styles.userTable}>
          <thead>
            <tr>
              <th>Name</th>
              <th>Email</th>
              <th>Mobile</th>
              <th>Address</th>
              <th>Role</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {filteredUsers.map(user => (
              <tr key={user.userId}>
                <td>{user.userName}</td>
                <td>{user.email}</td>
                <td>{user.mobileNo}</td>
                <td>{`${user.block}-${user.floor}-${user.doorNo}`}</td>
                <td>
                  <span className={`${styles.roleBadge} ${
                    user.role === 'Admin' ? styles.adminBadge :
                    user.role === 'Super Admin' ? styles.superAdminBadge :
                    styles.userBadge
                  }`}>
                    {user.role}
                  </span>
                </td>
                <td>
                  <button 
                    onClick={() => handleEditUser(user)}
                    className={styles.editButton}
                  >
                    Edit
                  </button>
                  <button
                    onClick={() => handleDeleteUser(user.userId)}
                    className={styles.deleteButton}
                    disabled={user.role === 'Super Admin'}
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <div className={styles.userFormContainer}>
        <h3>{editingUser ? 'Edit User' : 'Add New User'}</h3>
        <form onSubmit={editingUser ? handleUpdateUser : handleAddUser}>
          <div className={styles.formRow}>
            <div className={styles.formGroup}>
              <label>Full Name</label>
              <input
                type="text"
                name="userName"
                value={editingUser ? editingUser.userName : newUser.userName}
                onChange={handleInputChange}
                required
              />
            </div>
            <div className={styles.formGroup}>
              <label>Email</label>
              <input
                type="email"
                name="email"
                value={editingUser ? editingUser.email : newUser.email}
                onChange={handleInputChange}
                required
                disabled={editingUser !== null}
              />
            </div>
          </div>

          <div className={styles.formRow}>
            <div className={styles.formGroup}>
              <label>Mobile Number</label>
              <input
                type="number"
                name="mobileNo"
                value={editingUser ? editingUser.mobileNo : newUser.mobileNo}
                onChange={handleInputChange}
                required
              />
            </div>
            <div className={styles.formGroup}>
              <label>Password</label>
              <input
                type="password"
                name="password"
                value={editingUser ? editingUser.password : newUser.password}
                onChange={handleInputChange}
                required={!editingUser}
                placeholder={editingUser ? "Leave blank to keep current" : ""}
              />
            </div>
          </div>

          <div className={styles.formRow}>
            <div className={styles.formGroup}>
              <label>Block</label>
              <select
                name="block"
                value={editingUser ? editingUser.block : newUser.block}
                onChange={handleInputChange}
                required
              >
                <option value="">Select Block</option>
                <option value="A">A</option>
                <option value="B">B</option>
                <option value="C">C</option>
                <option value="D">D</option>
              </select>
            </div>
            <div className={styles.formGroup}>
              <label>Floor</label>
              <select
                name="floor"
                value={editingUser ? editingUser.floor : newUser.floor}
                onChange={handleInputChange}
                required
              >
                <option value="">Select Floor</option>
                <option value="1">1</option>
                <option value="2">2</option>
                <option value="3">3</option>
                <option value="4">4</option>
              </select>
            </div>
            <div className={styles.formGroup}>
              <label>Door No</label>
              <input
                type="number"
                name="doorNo"
                value={editingUser ? editingUser.doorNo : newUser.doorNo}
                onChange={handleInputChange}
                required
              />
            </div>
          </div>

          <div className={styles.formRow}>
            <div className={styles.formGroup}>
              <label>Role</label>
              <select
                name="role"
                value={editingUser ? editingUser.role : newUser.role}
                onChange={handleInputChange}
                required
              >
                <option value="User">User</option>
                <option value="Admin">Admin</option>
              </select>
            </div>
          </div>

          <div className={styles.formActions}>
            {editingUser ? (
              <>
                <button type="submit" className={styles.updateButton}>
                  Update User
                </button>
                <button
                  type="button"
                  onClick={cancelEdit}
                  className={styles.cancelButton}
                >
                  Cancel
                </button>
              </>
            ) : (
              <button type="submit" className={styles.addButton}>
                Add User
              </button>
            )}
          </div>
        </form>
      </div>
    </div>
  );
};

export default ManageUsers;
