import React, { useEffect, useState } from 'react';
import styles from './ManageUsers.module.css';
import axios from 'axios';

const ManageUsers = () => {
  const [users, setUsers] = useState([]);
  const [newUser, setNewUser] = useState({ flat: '', name: '', contact: '' });

  useEffect(() => {
    axios.get('http://localhost:8080/users/data')
      .then(response => setUsers(response.data))
      .catch(error => console.error('Error fetching users:', error));
  }, []);

  const handleChange = (e) => {
    setNewUser({ ...newUser, [e.target.name]: e.target.value });
  };

  const handleAddUser = (e) => {
  e.preventDefault();
  if (newUser.flat && newUser.name && newUser.contact) {
    axios.post('http://localhost:8080/users/save', newUser)
      .then(() => {
        // After saving, fetch updated user list
        return axios.get('http://localhost:8080/users/data');
      })
      .then(response => {
        setUsers(response.data); // Refresh state with updated list
        setNewUser({ flat: '', name: '', contact: '' }); // Clear form
      })
      .catch(error => console.error('Error adding user:', error));
  }
};


  const handleDeleteUser = (userId) => {
    axios.delete(`http://localhost:8080/users/delete/${userId}`)
      .then(() => {
        setUsers(users.filter(user => user.userId !== userId));
      })
      .catch(error => console.error('Error deleting user:', error));
  };

  return (
    <div className={styles.userList}>
      <h3>Manage Users</h3>
      <table border="2px" className={styles.userTable}>
        <thead>
          <tr>
            <th>Flat No</th>
            <th>Name</th>
            <th>Contact</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user, index) => (
            <tr key={index}>
              <td>{newUseruser.flat}</td>
              <td>{user.name}</td>
              <td>{user.contact}</td>
              <td>
                <button onClick={() => handleDeleteUser(user.userId)} className={styles.deleteButton}>
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <form onSubmit={handleAddUser} className={styles.userForm}>
        <h4>Add New User</h4>
        <input
          type="text"
          name="flat"
          placeholder="Flat No"
          value={newUser.flat}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="name"
          placeholder="Name"
          value={newUser.name}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="contact"
          placeholder="Contact"
          value={newUser.contact}
          onChange={handleChange}
          required
        />
        <button type="submit" className={styles.addButton}>Add User</button>
      </form>
    </div>
  );
};

export default ManageUsers;
