import React, { useEffect, useState } from 'react';
import styles from './ViewRequest.module.css';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const ViewRequest = () => {
  const [requests, setRequests] = useState([]);
  const navigate = useNavigate();


  useEffect(() => {
    axios.get('http://localhost:8080/request/data')
      .then((res) => {
        setRequests(res.data);
      })
      .catch((err) => {
        console.error('Error fetching requests:', err);
      });
  }, []);

  const handleSubmit = (selectedRequest) => {
    navigate('/requestdetail', { state: { request: selectedRequest } });
  };

  return (
    <div className={styles.container}>
      <h3 className={styles.heading}>My Service Requests</h3>

      {requests.length === 0 ? (
        <p className={styles.noRequests}>No requests available.</p>
      ) : (
        <ul className={styles.list}>
          {requests.map((req, index) => (
            <li key={index} className={styles.listItem}>
              <strong>Service:</strong> {req.serviceName || req.serviceId}<br />
              <strong>Description:</strong> {req.description}<br />
              <strong>Status:</strong> {req.status}<br />
              <button className={styles.button} onClick={() => handleSubmit(req)}>View Details</button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default ViewRequest;
