import React, { useState, useEffect } from 'react';
import axios from 'axios';
import styles from './ManageServices.module.css';
import { data } from 'react-router-dom';

const ManageServices = () => {
  const [services, setServices] = useState([]);
  const [newService, setNewService] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  // Fetch services on component mount
  useEffect(() => {
    axios.get('http://localhost:8080/api/services')
      .then(response => {
        setServices(response.data);
        setLoading(false);
      })
      .catch(error => {
        setError('Failed to load services');
        setLoading(false);
      });
  }, []);

  const handleAddService = () => {
  if (!newService.trim()) {
    setError('Service name cannot be empty');
    return;
  }

  const serviceData = {
    serviceName: newService 
    // Don't include serviceId - let backend generate it
  };

  axios.post('http://localhost:8080/api/services', serviceData)
    .then(response => {
      console.log(data);
      setNewService('');
      setError('');
      setSuccess('Service added successfully');
      // Refresh the services list
      axios.get('http://localhost:8080/api/services')
        .then(res => setServices(res.data));
    })
    .catch(error => {
      setError(error.response?.data?.message || 'Failed to add service');
    });
};

  const handleDeleteService = (serviceId) => {
    if (window.confirm('Are you sure you want to delete this service?')) {
      axios.delete(`http://localhost:8080/api/services/${serviceId}`)
        .then(() => {
          setSuccess('Service deleted successfully');
          // Refresh the services list
          axios.get('http://localhost:8080/api/services')
            .then(res => setServices(res.data));
        })
        .catch(error => {
          setError('Failed to delete service');
        });
    }
  };

  if (loading) return <div>Loading services...</div>;

  return (
    <div className={styles.container}>
      <h2>Manage Services</h2>
      
      {error && <div className={styles.error}>{error}</div>}
      {success && <div className={styles.success}>{success}</div>}
      
      <div className={styles.addForm}>
        <input
          type="text"
          value={newService}
          onChange={(e) => setNewService(e.target.value)}
          placeholder="Enter service name"
        />
        <button onClick={handleAddService}>Add Service</button>
      </div>
      
      <div className={styles.servicesList}>
        {services.map(service => (
          <div key={service.serviceId} className={styles.serviceItem}>
            <span>{service.serviceName}</span>
            <span>{service.status}</span>
            <button 
              onClick={() => handleDeleteService(service.serviceId)}
              className={styles.deleteBtn}
            >
              Delete
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ManageServices;
