import React, { useState, useEffect } from "react";

const Subject = () => {
    const [studentData, setStudentData] = useState(null);
    const [subjectCounts, setSubjectCounts] = useState([]);
    const [semesters, setSemesters] = useState([]);
    const [showSubjectInputs, setShowSubjectInputs] = useState(false);
    const [cgpa, setCgpa] = useState(null);

    useEffect(() => {
        const form = JSON.parse(localStorage.getItem("studentForm"));
        if (form) {
            setStudentData(form);
            setSubjectCounts(Array(Number(form.semCount)).fill(""));
        }
    }, []);

    const handleSubjectCountChange = (index, value) => {
        const updatedCounts = [...subjectCounts];
        updatedCounts[index] = value;
        setSubjectCounts(updatedCounts);
    };

    const generateSubjectInputs = () => {
        const tempSemesters = subjectCounts.map((count, semIndex) => ({
            semesterNumber: semIndex + 1,
            subjects: Array(Number(count))
                .fill()
                .map(() => ({
                    subjectName: "",
                    grade: "",
                    credits: "",
                })),
        }));
        setSemesters(tempSemesters);
        setShowSubjectInputs(true);
    };

    const handleSubjectChange = (semIndex, subjIndex, field, value) => {
        const updated = [...semesters];
        updated[semIndex].subjects[subjIndex][field] = value;
        setSemesters(updated);
    };

    const convertGradeToPoint = (grade) => {
        switch (grade.toUpperCase()) {
            case "A":
                return 10;
            case "B":
                return 8;
            case "C":
                return 6;
            case "D":
                return 4;
            case "E":
                return 2;
            default:
                return 0;
        }
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        let totalPoints = 0;
        let totalCredits = 0;

        semesters.forEach((sem) => {
            sem.subjects.forEach((subj) => {
                const points = convertGradeToPoint(subj.grade);
                const credits = Number(subj.credits);
                totalPoints += points * credits;
                totalCredits += credits;
            });
        });

        setCgpa(totalCredits > 0 ? (totalPoints / totalCredits).toFixed(2) : 0);
    };

    return (
        <div>
            <h2>Enter Subjects Information</h2>

            {/* Step 1: Number of subjects per semester */}
            {!showSubjectInputs && subjectCounts.length > 0 && (
                <div>
                    {subjectCounts.map((count, index) => (
                        <div key={index}>
                            <label>Semester {index + 1} - Number of Subjects:</label>
                            <input
                                type="number"
                                value={count}
                                onChange={(e) =>
                                    handleSubjectCountChange(index, e.target.value)
                                }
                                min="1"
                                required
                            />
                        </div>
                    ))}
                    <button type="button" onClick={generateSubjectInputs}>
                        Next
                    </button>
                </div>
            )}

            {/* Step 2: Subject input boxes */}
            {showSubjectInputs && (
                <form onSubmit={handleSubmit}>
                    {semesters.map((sem, semIndex) => (
                        <div
                            key={semIndex}
                            style={{
                                border: "1px solid gray",
                                margin: "10px",
                                padding: "10px",
                            }}
                        >
                            <h3>Semester {sem.semesterNumber}</h3>
                            {sem.subjects.map((subj, subjIndex) => (
                                <div
                                    key={subjIndex}
                                    style={{ display: "flex", gap: "10px", marginBottom: "5px" }}
                                >
                                    <input
                                        type="text"
                                        placeholder="Subject Name"
                                        value={subj.subjectName}
                                        onChange={(e) =>
                                            handleSubjectChange(
                                                semIndex,
                                                subjIndex,
                                                "subjectName",
                                                e.target.value
                                            )
                                        }
                                        required
                                    />
                                    <select
                                        value={subj.grade}
                                        onChange={(e) =>
                                            handleSubjectChange(
                                                semIndex,
                                                subjIndex,
                                                "grade",
                                                e.target.value
                                            )
                                        }
                                        required
                                    >
                                        <option value="">Select Grade</option>
                                        <option value="O">O</option>
                                        <option value="A+">A+</option>
                                        <option value="A">A</option>
                                        <option value="B+">B+</option>
                                        <option value="B">B</option>
                                        <option value="C">C</option>
                                        <option value="D">D</option>
                                    </select>

                                    <input
                                        type="number"
                                        placeholder="Credits"
                                        value={subj.credits}
                                        onChange={(e) =>
                                            handleSubjectChange(
                                                semIndex,
                                                subjIndex,
                                                "credits",
                                                e.target.value
                                            )
                                        }
                                        required
                                    />
                                </div>
                            ))}
                        </div>
                    ))}
                    <button type="submit">Calculate CGPA</button>
                </form>
            )}

            {/* Step 3: Display CGPA */}
            {cgpa !== null && (
                <div style={{ marginTop: "20px" }}>
                    <h3>CGPA: {cgpa}</h3>
                </div>
            )}
        </div>
    );
};

export default Subject;
