import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";
import styles from "./RequestDetail.module.css";

const RequestDetail = () => {
  const { id } = useParams();            // request id from URL
  const navigate = useNavigate();
  const [request, setRequest] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchRequest = async () => {
      try {
        const res = await axios.get(`http://localhost:8080/requests/${id}`);
        setRequest(res.data);
      } catch (err) {
        console.error("Error loading request:", err);
        setError("Failed to load request details");
      } finally {
        setLoading(false);
      }
    };

    fetchRequest();
  }, [id]);

  const handleEdit = () => {
    // pass current request via state so EditRequest can pre-fill
    navigate(`/editrequest/${id}`, { state: { request } });
  };

  const handleDelete = async () => {
    if (!window.confirm("Are you sure you want to delete this request?")) return;

    try {
      await axios.delete(`http://localhost:8080/requests/${id}`);
      alert("Request deleted successfully");
      navigate("/viewrequest");   // go back to list page
    } catch (err) {
      console.error("Delete error:", err);
      alert(
        `Failed to delete request: ${err.response?.status || err.message || "Unknown error"
        }`
      );
    }
  };

  const handleBack = () => {
    navigate("/viewrequest");
  };

  if (loading) {
    return <div className={styles.loading}>Loading...</div>;
  }
  if (error) {
    return <div className={styles.error}>{error}</div>;
  }
  if (!request) {
    return <div className={styles.error}>Request not found</div>;
  }

  return (
    <div className={styles.container}>
      <h2>Request Detail</h2>

      <div className={styles.field}>
        <label>Service Type:</label>
        <span>{request.serviceName || request.serviceId}</span>
      </div>

      <div className={styles.field}>
        <label>Description:</label>
        <span>{request.description}</span>
      </div>

      <div className={styles.field}>
        <label>Status:</label>
        <span>{request.status}</span>
      </div>

      <div className={styles.field}>
        <label>Created Date:</label>
        <span>{String(request.createDate)}</span>
      </div>

      <div className={styles.field}>
        <label>Expected Completion:</label>
        <span>{String(request.exceptCompletionDate || "Not specified")}</span>
      </div>

      <div className={styles.buttonGroup}>
        <button onClick={handleEdit}>Edit</button>
        <button className={styles.delete} onClick={handleDelete}>
          Delete
        </button>
        <button className={styles.back} onClick={handleBack}>
          Back to Requests
        </button>
      </div>
    </div>
  );
};

export default RequestDetail;
