import React, { useState, useEffect } from 'react';
import styles from './ManageServices.module.css';
import axios from 'axios';

const ManageServices = () => {
  const [services, setServices] = useState([]);
  const [newService, setNewService] = useState('');

  useEffect(() => {
    axios.get('http://localhost:8080/request/data').then((response)=>{
      console.log(response.data);
      
    })
  }, []);

  const addService = () => {
    if (newService.trim()) {
      axios.post('http://localhost:8080/request/data', { name: newService })
        .then(response => {
          setServices([...services, response.data]); 
          setNewService('');
        })
        .catch(error => console.error('Error adding service:', error));
    }
  };
  const removeService = (serviceId) => {
    axios.delete(`http://localhost:8080/api/services/${serviceId}`)
      .then(() => {
        setServices(services.filter(service => service.service_id !== serviceId));
      })
      .catch(error => console.error('Error deleting service:', error));
  };

  return (
    <div className={styles.services}>
      <h3>Manage Services</h3>
      <input
        type="text"
        placeholder="New Service"
        value={newService}
        onChange={(e) => setNewService(e.target.value)}
      />
      <button onClick={addService}>Add Service</button>
      <ul>
        {services.map(service => (
          <li key={service.service_id}>
            {service.name}
            <button onClick={() => removeService(service.service_id)}>Remove</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ManageServices;
