import React, { useState } from 'react';
import axios from 'axios'; // Import Axios
import './Student.css'; // Assuming you have this CSS file

function StudentGradingSystem() {
  const [studentInfo, setStudentInfo] = useState({
    name: '',
    roll: '',
    dept: '',
    semCount: '',
  });
  const [semesters, setSemesters] = useState([]);
  const [results, setResults] = useState(null);
  const [message, setMessage] = useState(''); // For success/error messages

  const handleStudentInfoChange = (e) => {
    setStudentInfo({ ...studentInfo, [e.target.id]: e.target.value });
  };

  const generateSemesters = () => {
    const count = parseInt(studentInfo.semCount);
    if (isNaN(count) || count <= 0) {
      setSemesters([]);
      return;
    }
    const newSemesters = Array.from({ length: count }, (_, i) => ({
      id: i + 1,
      subjectCount: '',
      subjects: [],
    }));
    setSemesters(newSemesters);
  };

  const handleSubjectCountChange = (semId, e) => {
    const newSemesters = semesters.map((sem) =>
      sem.id === semId ? { ...sem, subjectCount: e.target.value } : sem
    );
    setSemesters(newSemesters);
  };

  const generateSubjects = (semId) => {
    const newSemesters = semesters.map((sem) => {
      if (sem.id === semId) {
        const count = parseInt(sem.subjectCount);
        if (isNaN(count) || count <= 0) {
          return { ...sem, subjects: [] };
        }
        const newSubjects = Array.from({ length: count }, () => ({
          name: '',
          grade: 'O',
          credit: '',
        }));
        return { ...sem, subjects: newSubjects };
      }
      return sem;
    });
    setSemesters(newSemesters);
  };

  const handleSubjectChange = (semId, subIndex, e) => {
    const newSemesters = semesters.map((sem) => {
      if (sem.id === semId) {
        const newSubjects = sem.subjects.map((sub, index) =>
          index === subIndex ? { ...sub, [e.target.name]: e.target.value } : sub
        );
        return { ...sem, subjects: newSubjects };
      }
      return sem;
    });
    setSemesters(newSemesters);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setMessage(''); // Clear previous messages

    const studentData = {
      name: studentInfo.name,
      roll: studentInfo.roll,
      dept: studentInfo.dept,
      semesters: semesters.map((sem) =>
        sem.subjects.map((sub) => ({
          name: sub.name,
          grade: sub.grade,
          credit: parseInt(sub.credit),
        }))
      ),
    };

    // API Call to Spring Backend using Axios
    axios.post('http://localhost:8080/api/students/calculate-and-save', studentData)
      .then((response) => {
        setResults(response.data);
        setMessage('CGPA calculated and student data saved successfully!');
      })
      .catch((error) => {
        console.error('Error calculating CGPA or saving data:', error);
        setMessage('Failed to calculate CGPA or save data. Please try again.');
        setResults(null); // Clear results on error
      });
  };

  return (
    <div className="container">
      <h1>Student Grading System</h1>
      <form id="gradingForm" onSubmit={handleSubmit}>
        <div className="section">
          <h2>Student Info</h2>
          <input
            type="text"
            id="name"
            placeholder="Student Name"
            required
            value={studentInfo.name}
            onChange={handleStudentInfoChange}
          />
          <input
            type="number"
            id="roll"
            placeholder="Roll Number"
            required
            value={studentInfo.roll}
            onChange={handleStudentInfoChange}
          />
          <input
            type="text"
            id="dept"
            placeholder="Department"
            required
            value={studentInfo.dept}
            onChange={handleStudentInfoChange}
          />
          <input
            type="number"
            id="semCount"
            placeholder="Number of Semesters"
            required
            value={studentInfo.semCount}
            onChange={handleStudentInfoChange}
          />
          <button type="button" onClick={generateSemesters}>
            Next
          </button>
        </div>
        <div id="semestersSection">
          {semesters.map((sem) => (
            <div className="semester" key={sem.id}>
              <h3>Semester {sem.id}</h3>
              <input
                type="number"
                className="subject-count"
                data-sem={sem.id}
                placeholder="Number of Subjects"
                required
                value={sem.subjectCount}
                onChange={(e) => handleSubjectCountChange(sem.id, e)}
              />
              <button type="button" onClick={() => generateSubjects(sem.id)}>
                Add Subjects
              </button>
              <div className="subjects" id={`subjects-${sem.id}`}>
                {sem.subjects.map((sub, subIndex) => (
                  <div className="subject" key={subIndex}>
                    <input
                      type="text"
                      name="name"
                      placeholder="Subject Name"
                      required
                      value={sub.name}
                      onChange={(e) => handleSubjectChange(sem.id, subIndex, e)}
                    />
                    <select
                      name="grade"
                      required
                      value={sub.grade}
                      onChange={(e) => handleSubjectChange(sem.id, subIndex, e)}
                    >
                      <option value="O">O</option>
                      <option value="A+">A+</option>
                      <option value="A">A</option>
                      <option value="B+">B+</option>
                      <option value="B">B</option>
                      <option value="C">C</option>
                      <option value="D">D</option>
                    </select>
                    <input
                      type="number"
                      name="credit"
                      placeholder="Credits"
                      required
                      value={sub.credit}
                      onChange={(e) => handleSubjectChange(sem.id, subIndex, e)}
                    />
                  </div>
                ))}
              </div>
            </div>
          ))}
        </div>
        <button type="submit">Calculate CGPA & Save</button>
      </form>

      {message && (
        <div style={{ marginTop: '20px', padding: '10px', borderRadius: '5px', backgroundColor: message.includes('successfully') ? '#d4edda' : '#f8d7da', color: message.includes('successfully') ? '#155724' : '#721c24' }}>
          {message}
        </div>
      )}

      <div id="resultSection">
        {results && (
          <>
            <h2>Result for {results.name}</h2>
            <p>Roll No: {results.roll}</p>
            <p>Department: {results.dept}</p>
            {results.semesterSgpas &&
              results.semesterSgpas.map((sgpa, index) => (
                <p key={index}>Semester {index + 1} SGPA: {sgpa.toFixed(2)}</p>
              ))}
            <h3>Final CGPA: {results.cgpa.toFixed(2)}</h3>
            <h3>Classification: {results.classification}</h3>
          </>
        )}
      </div>
    </div>
  );
}

export default StudentGradingSystem;
