import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import styles from './RaiseRequest.module.css';

const RaiseRequest = () => {
  const [services, setServices] = useState([]);
  const [selectedService, setSelectedService] = useState('');
  const [description, setDescription] = useState('');
  const [message, setMessage] = useState('');
  const [isError, setIsError] = useState(false);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();
  const userId = localStorage.getItem('userId');

  useEffect(() => {
    axios.get('http://localhost:8080/api/services/available')
      .then(response => {
        setServices(response.data);
        setLoading(false);
      })
      .catch(error => {
        setMessage('Failed to load services');
        setIsError(true);
        setLoading(false);
      });
  }, []);

  const handleSubmit = () => {
    if (!selectedService || !description) {
      setMessage('Please fill all fields');
      setIsError(true);
      setTimeout(() => setMessage(''), 3000);
      return;
    }

    const requestData = {
      serviceId: parseInt(selectedService),
      userId: parseInt(userId),
      description: description,
      status: "Pending"
    };

    axios.post('http://localhost:8080/api/requests', requestData)
      .then(response => {
        setSelectedService('');
        setDescription('');
        setIsError(false);
        setMessage('Request submitted successfully!');
        setTimeout(() => navigate('/userdashboard'), 2000);
      })
      .catch(error => {
        setMessage(error.response?.data?.message || 'Failed to submit request');
        setIsError(true);
      });
  };

  if (loading) return <div>Loading services...</div>;

  return (
    <div className={styles.container}>
      <h2>Raise New Request</h2>
      
      {message && (
        <div className={isError ? styles.error : styles.success}>
          {message}
        </div>
      )}
      
      <div className={styles.formGroup}>
        <label>Select Service:</label>
        <select
          value={selectedService}
          onChange={(e) => setSelectedService(e.target.value)}
          required
        >
          <option value="">-- Select a service --</option>
          {services.map(service => (
            <option key={service.serviceId} value={service.serviceId}>
              {service.serviceName}
            </option>
          ))}
        </select>
      </div>
      
      <div className={styles.formGroup}>
        <label>Description:</label>
        <textarea
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          placeholder="Describe your request..."
          required
        />
      </div>
      
      <div className={styles.buttonGroup}>
        <button onClick={handleSubmit}>Submit Request</button>
        <button onClick={() => navigate('/userdashboard')}>Cancel</button>
      </div>
    </div>
  );
};

export default RaiseRequest;
