import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import styles from './ViewRequest.module.css';

const ViewRequest = () => {
  const userId = localStorage.getItem("userId");
  const [requests, setRequests] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchRequests = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/requests/user/${userId}`);
        setRequests(response.data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };
    fetchRequests();
  }, [userId]);

  const handleViewDetails = (requestId) => {
    navigate(`/requestdetail/${requestId}`);
  };

  if (loading) return <div className={styles.container}>Loading...</div>;
  if (error) return <div className={styles.container}>Error: {error}</div>;

  return (
    <div className={styles.container}>
      <h3 className={styles.heading}>View My Requests</h3>
      {requests.length === 0 ? (
        <p className={styles.noRequests}>No requests yet.</p>
      ) : (
        <div className={styles.requestList}>
          {requests.map((request) => (
            <div key={request.requestId} className={styles.requestItem}>
              <div className={styles.requestInfo}>
                <span>Service ID: {request.serviceId}</span>
                <span>Status: {request.status}</span>
              </div>
              <button 
                onClick={() => handleViewDetails(request.requestId)}
                className={styles.detailsButton}
              >
                View Details
              </button>
            </div>
          ))}
        </div>
      )}
      <button onClick={() => navigate('/userdashboard')} className={styles.backButton}>
        Back to Dashboard
      </button>
    </div>
  );
};

export default ViewRequest;
