import React from "react";
import appan from './appan.jpg';
import lord from './lord.jpg';
import muruga from './muruga.jfif';
import Simple from "./Simple";

function Muruga() {
  const courses = [
    {
      name: "Murugan",
      place: "Thiruchendhur",
      img: appan,
      mantra: "Om",
    },
    {
      name: "Kathirvela",
      place: "Palani",
      img: lord,
      mantra: "Saravana bhava",
    },
    {
      name: "Senthil",
      place: "Thovalai",
      img: muruga,
      mantra: "Arohara",
    }
  ];

  const MurugaList = courses.map((simple, index) => (
    <Simple
      key={index}
      name={simple.name}
      place={simple.place}
      img={simple.img}
      mantra={simple.mantra}
    />
  ));

  return <>{MurugaList}</>;
}

export default Muruga;
