import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';
import styles from './EditForm.module.css';

const EditForm = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const student = location.state?.student;

  const [form, setForm] = useState({
    name: '',
    dept: ''
  });

  useEffect(() => {
    if (student) {
      setForm({
        name: student.name,
        dept: student.dept
      });
    }
  }, [student]);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleUpdate = (e) => {
    e.preventDefault();
    axios.put(`http://localhost:8080/student/update/${student.id}`, form).then((response) => {
        console.log('Updated:', response.data);
        navigate('/apis');
      })
  };

  return (
    <div className={styles.editFormContainer}>
      <form onSubmit={handleUpdate} className={styles.editForm}>
        <input type="text"name="name"value={form.name} onChange={handleChange} placeholder="Enter Name"/>
        <input type="text" name="dept" value={form.dept} onChange={handleChange} placeholder="Enter Department"/>
        <button type="submit">Update</button>
      </form>
    </div>
  );
};

export default EditForm;
