import React, { useState } from 'react'
import styles from './Application.module.css';

const Application = () => {
    const [input,setInput]=useState({
        name:"",
        class:"",
        dob:"",
        pname:"",
        address:"",
        phone:"",
        email:""

    })
    const handleChange=(e) =>{
        setInput({...input,[e.target.name]:e.target.value});
    }

    const handleSubmit=(e) =>{
       
        console.log(input);
        
    }
  return (
    <>
<form className={styles.container}>
    <h1>Student Registration Form</h1>
    <label htmlFor="name">Students Name</label>
    <input type="text" name="name" id="name" placeholder='Enter Your name'required value={input.name} onChange={handleChange}/> <br /><br />

    <label htmlFor="class">Class you want to apply for</label>
    <input type="text" name="class" id="class" required value={input.class} onChange={handleChange}/> <br /> <br />

    <label htmlFor="dob">Student's DOB</label>
    <input type="date" name="dob" id="dob" required value={input.dob} onChange={handleChange}/> <br /> <br />

    <label htmlFor="pname">Parent's/Guardian name</label>
    <input type="text" name="pname" id="pname" placeholder='Enter Parent name' required value={input.pname} onChange={handleChange}/> <br /><br />

    <label htmlFor="address">Current Address</label>
    <input type="text" name="address" id="address" placeholder='Enter address' required value={input.address} onChange={handleChange}/> <br /><br />

    <label htmlFor="phone">Phone No</label>
    <input type="tel" name="phone" id="phone" placeholder='### ### ####' required value={input.phone} onChange={handleChange}/> <br /><br />

    <label htmlFor="email">Email</label>
    <input type="email" name="email" id="email" placeholder='Enter an Email' required value={input.email} onChange={handleChange}/> <br /><br />

    <button type='submit'onClick={handleSubmit}>Submit</button>
    </form>
    </>
  )
}

export default Application
