import React, { useState, useEffect } from 'react';
import styles from './ManageServices.module.css';
import axios from 'axios';

const ManageServices = () => {
  const [newService, setNewService] = useState('');
  const [services, setServices] = useState([]);

  // Fetch all services on component mount
  useEffect(() => {
    fetchServices();
  }, []);

  const fetchServices = () => {
    axios.get('http://localhost:8080/request/data') // Adjust endpoint as needed
      .then((response) => {
        setServices(response.data);
      })
      .catch((error) => {
        console.error("Error fetching services:", error);
      });
  };

  const addService = () => {
    if (newService.trim() !== '') {
      const serviceData = {
        name: newService
      };

      axios.post(`http://localhost:8080/request/save`, serviceData)
        .then((response) => {
          alert("Service added");
          
          console.log('Service added:', response.data);
          setNewService('');
          fetchServices(); // Refresh list
        })
        .catch((error) => {
          console.error('Error adding service:', error);
        });
    }
  };

  const removeService = (serviceId) => {
    axios.delete(`http://localhost:8080/request/delete/${serviceId}`)
      .then(() => {
        console.log('Service deleted');
        fetchServices(); // Refresh list
      })
      .catch((error) => {
        console.error('Error deleting service:', error);
      });
  };

  return (
    <div className={styles.services}>
      <h3>Manage Services</h3>
      <input
        type="text"
        name='serviceName'
        placeholder="New Service"
        value={newService}
        onChange={(e) => setNewService(e.target.value)}
      />
      <button onClick={addService}>Add Service</button>

      <ul>
        {services.map((service) => (
          <li key={service.serviceId}>
            {service.serviceName}
            <button onClick={() => removeService(service.serviceId)}>Remove</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ManageServices;
