import React, { useState } from 'react';
import styles from './Login.module.css'; 
import { useNavigate, Link } from 'react-router-dom'; 
import axios from 'axios'; 
const Login = () => {
   const [form, setForm] = useState({ name: "", password: "", role: "" }); 
   const navigate = useNavigate(); 
   const handleChange = (e) => {
     setForm({ ...form, [e.target.name]: e.target.value });
     }; 
     const handleSubmit = (e) => {
       e.preventDefault(); 
       console.log('Login Details:', form); 
       axios.post("http://localhost:8080/users/login", form).then((Response) => {
         console.log(Response.data);
          switch (Response.data.role)
           {
             case 'Super Admin': navigate('/superadmindashboard'); break; case 'Admin': navigate('/admindashboard'); break; case 'User': navigate('/userdashboard'); break; default: alert('Invalid role selected'); } }).catch((error) => { console.error("Login error:", error); alert("Login failed. Check credentials."); }); setForm({ name: "", password: "", role: "" }); }; return (<><h1 className={styles.head}>My Colony</h1><form className={styles.login} onSubmit={handleSubmit}><h1>Login</h1><input type="email" name="email" placeholder="Email Address" value={form.email} onChange={handleChange} required/><input type="password" name="password" placeholder="Password" value={form.password} onChange={handleChange} required/><select name="role" value={form.role} onChange={handleChange} required><option value="">Select Role</option><option value="Admin">Admin</option><option value="Super Admin">Super Admin</option><option value="User">User</option></select><div className={styles.links}><Link to="/forgot">Forgot Password?</Link><Link to="/newuser">New User</Link></div><button type="submit">Log in</button></form></>); }; export default Login;
